class SubStringTest {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String str = "Hello";
		
		String a = str.substring(2, 4);  // ll
		
		String b = str.substring(0, 3);  // Hel
		
		String c = str.substring(4, 5);  // o
		
		System.out.println(a);
		System.out.println(b);
		System.out.println(c);

	}

}
